mapper.select:
select m.ID,m.CREATE_TIME ,m.TITLE,m.CONTENT,u.NAME username ,smr.status READ_FLAG from sys_message m left join sys_user u on u.USER_ID = m.CONTACT_USER inner join sys_message_receiver smr on smr.message_id = m.ID where m.MESSAGE_TYPE = 'SYS' and smr.receiver = #{userVo.userId,jdbcType=VARCHAR}
and date_format(m.CREATE_TIME,'%Y-%m-%d') >= #{query.startTime,jdbcType=DATE} and date_format(m.CREATE_TIME,'%Y-%m-%d') <= #{query.endTime,jdbcType=DATE}
and m.READ_FLAG = #{query.readFlag,jdbcType=VARCHAR}
