mapper.update:
update
sys_user set password=#{password,jdbcType=VARCHAR} where
LOGIN_ID=#{loginId,jdbcType=VARCHAR}

mapper.select:
SELECT u.*, sur.ROLE_ID role FROM sys_user u left join sys_user_role sur on u.USER_ID = sur.USER_ID WHERE 1 = 1
AND sur.USER_ID = #{userId,jdbcType=VARCHAR}
AND u.STATE != '9'
